$(document).ready(function() {
  $('#accordion .accordion-header').click(function() {
    const $this = $(this).parents(".accordion-item");
    $this.siblings().removeClass("open").find(".accordion-content").slideUp();
    $this.siblings().find(".accordion-header").removeClass("active");
    $this.toggleClass("open");
    $this.children(".accordion-header").toggleClass("active");
    $this.children(".accordion-content").slideToggle();
  });

  $('#accordion .accordion-item.open .accordion-header').addClass("active");
  $('#accordion .accordion-item.open .accordion-content').show();
});